import { jwtDecode } from 'jwt-decode'
import { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'

export default function Profile() {
    const navigation = useNavigate()
    const [MyActivities,setMyActivities] = useState([])


    const token = localStorage.getItem('userToken')
    useEffect(() => {
        if (token == "null") {
            navigation("/Login")
            return
        }
        // Si el token no es null hacemos la request aca

        const decoded = jwtDecode(token)
        let userid = decoded.userid
        let url = "/user/" + userid + "/activities"

        const headers = {
            'Authorization': localStorage.getItem('userToken'),
            'Content-Type': 'text/plain',
        };


        try {
            fetch(url, {
                method: 'GET',
                headers: headers
            }).then((res) => {
                if (res.status == 401) {
                    console.log("toke is not valid")
                    localStorage.setItem('userToken',null)
                    navigation("/Login")
                }
                return (res.json());
            }).then((data) => {
                console.log(data)
                setMyActivities(data)
            })
        } catch (error) {
            console.log("404:Could not Fetch")
            return
        }

    },[])
    return (
        <div>
            <h1>Mis Actividades</h1>
            {MyActivities.map((myactividad)=>{
                return (
                    <div key={myactividad.id}>
                        <h4>myactividad.name</h4>
                    </div>
                )
            })}
        </div>
    )
}
