import { Link, useNavigate } from "react-router-dom";
import { useEffect,useState } from "react";
import { jwtDecode } from 'jwt-decode';
import './styles/activities.css';


function Home() {
    const [selectedActivity,setselectedActivity] = useState(null)
    const [Actividades, setActividades] = useState([])
    const [isJWTvalid,setisJWTvalid] = useState(false)
    const navigation = useNavigate();
    useEffect(()=> {
        try {
            const token = localStorage.getItem('userToken') 
            const headers = {
                'Authorization': token,
               'Content-Type': 'text/plain',
            };
            fetch("/actividades",{
                method: 'GET',
                headers: headers
            }).then((res)=> {
                res.headers.get("istokenvalid") == "true" ? setisJWTvalid(true) : setisJWTvalid(false)
                res.headers.get("istokenvalid") == "true" ? " " : localStorage.setItem('userToken',null)
                return(res.json());
            }).then((data)=>{
                console.log(data)
                setActividades(data);
                var decodedToken;
                if (isJWTvalid) {
                    decodedToken = jwtDecode(token);
                }
            })
        } catch (error) {
            console.log("404:Could not Fetch")
            return
        }
    }, []);

    function SearchByParam(event) {
        event.preventDefault();
        const param = document.getElementById("search-activity").value
        const url = 'actividades?keyword='+param;
        navigation(url)
    }

    function Horarios(props) {

        var close = false;
        if (close == false) {
        return (
            <div onClick={() => {setselectedActivity(null)}} className="Inscripcion-horarios">
            <div className="inscription-container">
            <h4>Horarios</h4>
            <div className="Horarios">
                {props.actividad.activity_hours != null ? props.actividad.activity_hours.map((hour) => {
                return (
                    <div key={hour.id} className="hour">
                        <p>{hour.day}</p>
                        <p>{hour.hour_start}{hour.hour_start > 1100 && hour.hour_start <= 2300 ? "PM" : "AM"}</p>
                        <p>{hour.hour_finish}{hour.hour_finish > 1100 && hour.hour_finish <= 2300 ? "PM" : "AM"}</p>
                        <button>Inscribirse</button>
                    </div>
                );
            }) : <p>No hay horarios disponibles</p>}
            </div>
            </div>
        </div>
        )  
        } else {
            return false;
        }
    };

    return ( 
        <div className="Home-page">
            <form onSubmit={SearchByParam}><input placeholder="Buscar por palabra clave o categoria" type="text" id="search-activity"></input></form>
            <div className="Activities-container">
            {Actividades.map((actividad)=>{
                return(
                <div key={actividad.id} className="activity">
                    <h3>{actividad.name}</h3>
                    <p>{actividad.description}</p>
                    <p>Tipo de Actividad: {actividad.activitytype}</p>
                    <p>Profesor: {actividad.coach_name}</p>
                    <button onClick={ ()=> setselectedActivity(actividad.id)}>Realizar Inscripcion</button>
                    {selectedActivity == actividad.id && <Horarios actividad={actividad}/>}
                </div>
            )})}
            </div>
        </div>
    )
};




export default Home;
